import React ,{useState}from "react";
import LoginForm from "./Login_form";
 import Profile from "./Profile";
import Account from './Account.jsx';
// import Createacunt from "./Createacunt.JSX";

import $ from 'jquery';


function App() {

    const[userdata,setuserdata]=useState({
   username:"",
    name:"",
    Profile_img:"",
   phone:"",
    bio:"",
   email:"",
   birth:""
    })

   function acc()
   {
    $("#account").show();
    $("#login").hide();
   }

  return(
    <>

                <div id="login">


                 <LoginForm onSubmit={login} accunt={acc}/>
                 </div>
                  <div id="profile" style={{display:"none"}}>
                 <Profile img={userdata.Profile_img}
                  username={userdata.username}
                   name={userdata.name} 
                   phone={userdata.phone}
                   email={userdata.email}
                   bio={userdata.bio}
                   birth={userdata.birth}/>
                 </div> 
                <div id="account" style={{display:"Node"}}>
                 <Account onSubmit={account} style={{display:"none"}}/>  
                </div>





     </>
  )

  function account(data)
  {

    const formData = new FormData();
    // let result =data.username.trim();
    formData.append('username',data.username);


     formData.append('name',data.name);
     formData.append('password',data.password);
     formData.append('email',data.email);
     formData.append('bio',data.bio);
     formData.append('birth',data.birth);
     formData.append('phone',data.phone);
     formData.append('img',data.img);
     formData.append('action',data.action);

     if(formData.get('username')!=="undefined")
     {
      backand(formData);
     }else
     {
      console.log("no data");
     }

  }
  function login(data)
   {
    const formData = new FormData();

    formData.append('username',data.username);
    formData.append('password',data.password);
     backand(formData);
   }

   function backand(formData) {
    $.ajax({
       url: 'http://localhost/myprojects/backand/database.php',
      type: 'POST',
     data: formData,
     contentType: false,
     processData: false,
      success: function(response)
       {
        if(formData.get('action')==="insert")
       {
        console.log('File uploaded successfully:', response);

         setuserdata({
               username:response.$username,
               name:response.$name,
               Profile_img:response.$img,
               phone:response.$phone,
               bio:response.$bio,
               email:response.email,
               birth:response.$birth

         }) 
         $('#account').hide();
        $('#profile').show();

      }
      }
    });
  }

}


export default App;
