import React ,{useState}from "react";
import LoginForm from "./Login_form";
 import Profile from "./Profile";
import Account from './Account.jsx';
// import Createacunt from "./Createacunt.JSX";

import $ from 'jquery';


function App() {

    const[userdata,setuserdata]=useState({
   username:"",
    name:"",
    Profile_img:"",
   phone:"",
    bio:"",
   email:""
    })

   function acc(data)
   {
    console.log(data);
   }

  return(
    <>

                <div id="login">


                 <LoginForm onSubmit={login} onClick={acc}/>
                 </div>
                  <div id="profile" style={{display:"none"}}>
                 <Profile img={userdata.Profile_img}
                  username={userdata.username}
                   name={userdata.name} 
                   phone={userdata.phone}
                   email={userdata.email}
                   bio={userdata.bio} />
                 </div> 
                <div id="account">
                 <Account onSubmit={account}/>  
                </div>





     </>
  )

  function account(data)
  {

    const formData = new FormData();

    formData.append('username',data.username);
    formData.append('name',data.name);
     formData.append('password',data.password);
     formData.append('email',data.email);
     formData.append('bio',data.bio);
     formData.append('birth',data.birth);
     formData.append('phone',data.phone);
     formData.append('img',data.img);
     formData.append('action',data.action);
    backand(formData);
  }
  function login(data)
   {
    const formData = new FormData();
    formData.append('username',data.username);
    formData.append('password',data.password);
    backand(formData);
   }

  function backand(formData) {
    $.ajax({
      url: 'http://localhost/myprojects/backand/database.php',
      type: 'POST',
      data: formData,
      contentType: false,
      processData: false,
      success: function(response)
       {
        if(formData.get('action')==="insert")
        {
         console.log('File uploaded successfully:', response);
         $('#account').hide();
         $('#profile').show();

        }
      },
      error: function(error) {
        console.error('Error uploading file:', error);
      }

    });
  }

}


export default App;
