import React,{useState} from "react";
import LoginForm from "./Login_form";
import Profile from "./Profile";
import Account from './Account.jsx';
// import Createacunt from "./Createacunt.JSX";

import $ from 'jquery';


function App() {

   const[userdata,setuserdata]=useState({
    username:"",
    name:"",
    Profile_img:"",
    phone:"",
    bio:"",
   email:""
   })
   function login(data)
   {
    backand(data);
   }

  return(
    <>

                <div id="login">


                 <LoginForm onSubmit={login}/>
                 </div>
                 <div id="profile" style={{display:"none"}}>
                 <Profile img={userdata.Profile_img}
                  username={userdata.username}
                   name={userdata.name} 
                   phone={userdata.phone}
                   email={userdata.email}
                   bio={userdata.bio} />
                 </div>

                 <Account onSubmit={account}/>  







     </>
  )

  function account(data)
  {
    var formData = new FormData();
    formData.append('img',data.img);
    formData.append('username',data.username);
    formData.append('password',data.password);
    formData.append('action',data.action);
    backand(formData);
  }

  function backand(formData) {
    $.ajax({
      url: 'http://localhost/myprojects/backand/database.php',
      type: 'post',
      data: formData,
       processData: false,
      contentType: false,
      success: function (response) {
        if (formData.action === "submit") {
          var l = formData.username;
          if (response.$username === l) {
            setuserdata({
              name: response.$name,
              username: response.$username,
              bio: response.$bio,
              Profile_img: response.$img,
              phone: response.$phone,
              email: response.$email
            });
            $("#profile").show();
            $("#login").hide();
          }
        } else if (data.action === "insert") {
          console.log(response);
        }
      },
      error: function (error) {
        console.error('Error:', error);
      }
    });
  }

}


export default App;
