import React, { useState } from "react";
import LoginForm from "./Login_form";
import Profile from "./Profile";
import Account from "./Account.jsx";
import $ from 'jquery';

function App() {
    const [userdata, setuserdata] = useState({
        username: "",
        name: "",
        Profile_img: "",
        phone: "",
        bio: "",
        email: ""
    });

    const [send, newsend] = useState(null);

    function Submi(data) {
        backand(data);
    }

    function insert(img) {
        newsend(img);
        console.log(send);
    }

    function backand(data) {
        $.ajax({
            url: 'http://localhost/myprojects/backand/database.php',
            type: 'post',
            data: data,
            success: function (response) {
                var l = data.username;
                if (response.$username === l) {
                    setuserdata({
                        name: response.$name,
                        username: response.$username,
                        bio: response.$bio,
                        Profile_img: response.$img,
                        phone: response.$phone,
                        email: response.$email
                    });
                    $("#profile").show();
                    $("#login").hide();
                } else {
                    console.log("no match");
                }
            }
        });
    }

    return (
        <>
            <div id="login">
                <LoginForm onSubmit={Submi} />
            </div>
            <div id="profile" style={{ display: "none" }}>
                <Profile
                    img={userdata.Profile_img}
                    username={userdata.username}
                    name={userdata.name}
                    phone={userdata.phone}
                    email={userdata.email}
                    bio={userdata.bio}
                />
            </div>
            <Account onSubmit={insert} />
        </>
    );
}

export default App;
