import React,{useState,useEffect} from "react";
import LoginForm from "./Login_form";
import Profile from "./Profile";
import Account from './Account.jsx';
// import Createacunt from "./Createacunt.JSX";

import $ from 'jquery';


function App() {

   const[userdata,setuserdata]=useState({
    username:"",
    name:"",
    Profile_img:"",
    phone:"",
    bio:"",
    email:""
   })




  return(
    <>

                <div id="login">


                 <LoginForm onSubmit={Submi}/>
                 </div>
                 <div id="profile" style={{display:"none"}}>
                 <Profile img={userdata.Profile_img}
                  username={userdata.username}
                   name={userdata.name} 
                   phone={userdata.phone}
                   email={userdata.email}
                   bio={userdata.bio} />
                 </div>

                  <Account onSubmit={sub}/> 







     </>
  )


 function Submi(data){

     $.ajax({
       url:'http://localhost/myprojects/backand/database.php',
      type:'post',
      data:data,
    success: function(response) {
     var l= data.username;

      if(response.$username===l)
      {
     setuserdata({


      name:response.$name,
      username:response.$username,
      bio:response.$bio,
      Profile_img:response.$img,
      phone:response.$phone,
      email:response.$email


     })

     $("#profile").show();
     $("#login").hide();
    }else
    {
    console.log("no match");
    }
  }
   })
   }

}



export default App;
