import React,{useState} from "react";
import AccInput from "./AccInput";
function Account(onSubmit)
{
   
  const[fromdata,newformdata]=useState(
    {
      username:"",
      img:"my_img/profile_db.webp",
      name:"",
      bio:"",
      email:"",
      phone:"",
      password:"",
      birthday:""
    }
  )
   function uplodeimg(e)
   {
    
      const file = e.target.files[0];
      const reader = new FileReader();
      reader.onload = () => {
        newformdata({img:(reader.result)});
      };
  
      if (file) {
        reader.readAsDataURL(file);
      }
    
   }
    function submit(onSubmit)
    {
      onSubmit(fromdata);
    }
   function onchanghandel(e)
   {
    newformdata(function(pre){
      if(e.target.name==="username")
      {
        return{
        username:e.target.value,
        password:pre.password,
        birthday:pre.birthday,
        bio:pre.bio,
        name:pre.name,
        email:pre.email,
        phone:pre.phone,
        img:pre.img
        }
      }
      else if(e.target.name==="password")
      {
        return{
          username:pre.username,
        password:e.target.value,
        birthday:pre.birthday,
        bio:pre.bio,
        name:pre.name,
        email:pre.email,
        phone:pre.phone,
        img:pre.img
          }
      }else if(e.target.name==="name")
      {
        return{
          username:pre.username,
        password:pre.password,
        birthday:pre.birthday,
        bio:pre.bio,
        name:e.target.value,
        email:pre.email,
        phone:pre.phone,
        img:pre.img
          }

      }
      else if(e.target.name==="email")
      {
        return{
          username:pre.username,
        password:pre.password,
        birthday:pre.birthday,
        bio:pre.bio,
        name:pre.name,
        email:e.target.value,
        phone:pre.phone,
        img:pre.img
          }


      }
      else if(e.target.name==="bio")
      {
        return{
          username:pre.username,
        password:pre.password,
        birthday:pre.birthday,
        bio:e.target.value,
        name:pre.name,
        email:pre.email,
        phone:pre.phone,
        img:pre.img
          }
          

      }else if(e.target.name==="phone")
      {
        return{
          username:pre.username,
        password:pre.password,
        birthday:pre.birthday,
        bio:pre.bio,
        name:pre.name,
        email:pre.email,
        phone:e.target.value,
        img:pre.img
          }
          

      }else if(e.target.name==="birthday")
      {
        return{
          username:pre.username,
        password:pre.password,
        birthday:e.target.value,
        bio:pre.bio,
        name:pre.name,
        email:pre.email,
        phone:e.target.value,
        img:pre.img
          }
          

      }
    })
   }

   
    return(
        <>
  <header id="header" className="header fixed-top d-flex align-items-center">

    <div className="d-flex align-items-center justify-content-between">
      <a href="index.html" className="logo d-flex align-items-center">
         <span className="d-none d-lg-block">user-profile</span>
      </a>
     
    </div>
   
  </header>
  <main id="main" className="main">
    <section className="section profile">
      <div className="row">
        <div className="col-xl-4">
          <div className="card" style={{marginTop:"150px",marginLeft:"10PX"}}>
            <div className="card-body profile-card pt-4 d-flex flex-column align-items-center">

              <img src={fromdata.img} alt="Profile" id="profile_img" className="rounded-circle"/>
             
              <div className="social-links mt-2">
              <input type="file" id="fileinput" style={{ display: 'none' }} onChange={uplodeimg} />

<               button className="btn btn-primary btn-sm" title="Upload new profile image" onClick={() => { document.getElementById('fileinput').click(); return false; }}>
                    <i className="bi bi-upload"></i>
                  </button>
                <button className="btn btn-danger btn-sm" title="Remove my profile image"><i className="bi bi-trash"></i></button>
              </div>
            </div>
          </div>
       </div>

        <div className="col-xl-7 offset-xl-1">

          <div className="card">
            <div className="card-body pt-3">
        
              <ul className="nav nav-tabs nav-tabs-bordered">

                

                <li className="nav-item">
                  <p>create Profile</p>
                </li>

              

                

              </ul>
              <div className="tab-content pt-2">

                <div  id="profile-edit">

                 
                  <form method="post" id="alldata" onSubmit={submit}>
                    

                   <AccInput msg="enter username" type="text" id="username" name="username"  for="username" onChange={onchanghandel}/>
                    <AccInput msg="enter name" type="text" id="name" name="name" for="name" onChange={onchanghandel}/>
                    <AccInput msg="enter password" type="text" id="password" name="password" for="password" onChange={onchanghandel}/>
                    <AccInput msg="enter phone number" type="number" id="phone" name="phone" for="phone"onChange={onchanghandel}/>
                    <AccInput msg="enter phone bithda" type="date" id="birth" name="birth" for="birth"onChange={onchanghandel}/>
                    <AccInput msg="enter email email" type="email" id="email" name="email" for="email"onChange={onchanghandel}/>
                    
                

                    <div className="row mb-3">
                      <label for="bio" className="col-md-4 col-lg-3 col-form-label">bio</label>
                      <div className="col-md-8 col-lg-9">
                        <textarea name="bio" className="form-control" id="bio" style={{height: "100px"}}> </textarea>
                      </div>
                    </div>
                    <div className="text-center">
                      <button type="submit" className="btn btn-primary" id="submit" name="up">Save </button>
                    </div>
                  </form>
                
                </div>

               
                  
                

                

               

              </div>

            </div>
          </div>

        </div>
      </div>
    </section>
  

  </main>

        </>
    )
}
export default Account;
