import React, { useState } from "react";
import AccInput from "./AccInput";

function Account({ onSubmit }) {
  const [from, newform] = useState({
    username: "",
    name: ""
  });

  function event(e) {
    newform(function (pre) {
      if (e.target.name === "username") {
        return {
          ...pre,
          username: e.target.value
        };
      } else if (e.target.name === "name") {
        return {
          ...pre,
          name: e.target.value
        };
      }
    });
  }

  function submit(onSubmit) {
    onsubmit.preventDefault();
    onSubmit(from);
  }

  return (
    <>
      <main id="main" className="main">
        <section className="section profile">
          <div className="row">
            <div className="col-xl-7 offset-xl-1">
              <div className="card">
                <div className="card-body pt-3">
                  <ul className="nav nav-tabs nav-tabs-bordered">
                    <li className="nav-item">
                      <p>create Profile</p>
                    </li>
                  </ul>
                  <div id="profile-edit">
                    <form method="post" id="alldata" onSubmit={submit}>
                      <AccInput
                        msg="enter username"
                        type="text"
                        id="username"
                        name="username"
                        htmlFor="username"
                        onChange={event}
                        value={from.username}
                      />
                      <AccInput
                        msg="enter name"
                        type="text"
                        id="name"
                        name="name"
                        htmlFor="name"
                        onChange={event}
                        value={from.name}
                      />
                      <div className="text-center">
                        <button
                          type="submit"
                          className="btn btn-primary"
                          id="submit"
                          name="up"
                        >
                          Save
                        </button>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
      </main>
    </>
  );
}

export default Account;
