function App() {

  return(
    <>
  {/* // <!-- ======= Header ======= --> */}
  {/* <!-- End Header --> */}
                <div id="login">


                 <LoginForm onSubmit={Submit}/>
                 </div>
                 <div id="profile" style={{display:"none"}}>
                 <Profile/>
                 </div>





     </>
  )
 function Submit(data){

     $.ajax({
       url:'http://localhost/myprojects/backand/database.php',
      type:'post',
      data:data,
    success: function(response) {

      if(response==="metch")
      {
        $("#profile").show();
        $("#login").hide();

      }
     }
   })
   }

}



export default App;
